//请求方法
export const fetcha = (url,data) => {
    fetch(url,{
      method: 'POST', 
      body: JSON.stringify(data),
      headers: new Headers({
        'Content-Type': 'application/x-www-form-urlencoded'
      })
    })
    .then(res => res.json())
  }

//初始fetch方法
export const fetchb =  function* (url){
  const res = yield fetch(url)

  const data = yield res.json()

  return data

}

//搜索条件匹配fetch方法
export const fetchc =  function* (url,data){
  const res = yield fetch(url,{
      method: 'POST', 
      body: JSON.stringify(data),
      headers: new Headers({
        'Content-Type': 'application/x-www-form-urlencoded'
      })
  })
  const data1 = yield res.json()

  return data1

}


//添加key
export const change = function(response) {

  for (let k in response){
  response[k].key = response[k].id
  }
  return response

}


//fetch数据后 添加key
export const fetch1 = function* (){

  const response = yield fetchb('http://localhost:8080/')

  const data = yield change(response)

  return data

}


export const datamql = function* (val){
  const data1 = yield val.data

  const fedata = yield val.data2

  const res = yield fetcha(`http://localhost:8080/${fedata}`,data1)

  return res
}


export const datamql2 = function* (val){
  const data1 = yield val.data

  const fedata = yield val.data2

  yield fetchc(`http://localhost:8080/${fedata}`,data1)

  const data2 = yield fetch1()

  return data2
}
