import { put, takeEvery } from 'redux-saga/effects';
import { fetcha,fetchc,change,fetch1,datamql,datamql2} from '../Request/request'



//put 与actiontype相同
//Mysql 请求数据 (初始渲染  重置)
function* fetchUser() {
    const data = yield fetch1()

    yield put({type: "fetch", payload:data});   
}

//MYSQL 删除
function* mysqlDelFectch(val){

    const data2 = yield datamql2(val)

    yield put({type: "fetch", payload:data2});
}

//MYSQL 添加
function* mysqlAddFectch(val){

    const data2 = yield datamql(val)

    yield put ({type:'fetch' ,payload:data2})
}

//MYSQL 搜索
function* mysqlSearchFectch(val){

   const data = val.data
  //  const fedata = val.data2

  //  const res = yield fetch(`http://localhost:8080/${fedata}`, {
  //     method: 'POST', 
  //     body: JSON.stringify(data),
  //     headers: new Headers({
  //       'Content-Type': 'application/x-www-form-urlencoded'
  //     })
  //   })

    const response = yield fetchc('http://localhost:8080/search')

    // const response = yield res.json()

    const data2 = yield change(response)

    yield put ({type:'search' ,payload:data2})

  }



//MYSQL 修改
function* mysqlSaveFectch(val){
    const data2 = yield datamql2(val)

    yield put({type:'fetch',payload:data2})
}


//监听与组件type相同
 function* mySaga() {
   yield takeEvery("feh", fetchUser);
   yield takeEvery('del', mysqlDelFectch)
   yield takeEvery('add', mysqlAddFectch)
   yield takeEvery('sear', mysqlSearchFectch)
   yield takeEvery('sav', mysqlSaveFectch)
 }


 export default mySaga;
