import { put, takeEvery } from 'redux-saga/effects'


//put 与actiontype相同
function* fetchUser() {
       const res =  yield fetch('http://localhost:8080/')

       const response = yield res.json()
       for (let k in response){
        response[k].key = response[k].createdAt
      }


       yield put({type: "fetch", payload:response});

}



function* mysqlDelfectch(key){
  yield console.log(key.key)
  const res = yield fetch('http://localhost:8080/delete',{
    method: 'POST', // or 'PUT'
    body: JSON.stringify(key), // data can be `string` or {object}!
    headers: new Headers({
      'Content-Type': 'application/x-www-form-urlencoded'
    })
  })

  const data = yield res.json()
  yield put({type: "delete", payload:data});
}




//监听与组件type相同
 function* mySaga() {
   yield takeEvery("feh", fetchUser);
   yield takeEvery('del', mysqlDelfectch)

 }


 export default mySaga;
