import { put, takeEvery } from 'redux-saga/effects';
import { fetcha, fetchb,fetchc,change} from '../Request/request'



//put 与actiontype相同
//Mysql 请求数据 (初始渲染  重置)
function* fetchUser() {
       const response = yield fetchb('http://localhost:8080/')

       const data = yield(response)

       yield put({type: "fetch", payload:data});   
}

//MYSQL 删除
function* mysqlDelFectch(key){
      const id = yield key.key

      yield fetcha('http://localhost:8080/delete',id)

      //重新请求数据
      const response = yield fetchb('http://localhost:8080/')

      const data = yield(response)

      yield put({type: "delete", payload:data});
}

//MYSQL 添加
function* mysqlAddFectch(val){

      const data1 = yield val.data

      yield fetcha('http://localhost:8080/add',data1)

      const response = yield fetchb('http://localhost:8080/')

      const data = yield(response)

      yield put ({type:'create' ,payload:data})
}

//MYSQL 搜索
function* mysqlSearchFectch(data1){

      const name = yield data1.value

      const response = yield fetchc('http://localhost:8080/search',name)

      const data = yield(response)

      yield put ({type:'search' ,payload:data})
}

//MYSQL 修改
function* mysqlSaveFectch(val){
  const data1 = yield val.row

  yield fetcha('http://localhost:8080/change',data1)

  //重新请求数据
  const response = yield fetchb('http://localhost:8080/')

  const data = yield(response)

  yield put({type:'save',payload:data})
}


//监听与组件type相同
 function* mySaga() {
   yield takeEvery("feh", fetchUser);
   yield takeEvery('del', mysqlDelFectch)
   yield takeEvery('add', mysqlAddFectch)
   yield takeEvery('sear', mysqlSearchFectch)
   yield takeEvery('sav', mysqlSaveFectch)
 }


 export default mySaga;
