//请求方法
export const fetcha = (url,data) => {
    fetch(url,{
      method: 'POST', 
      body: JSON.stringify(data),
      headers: new Headers({
        'Content-Type': 'application/x-www-form-urlencoded'
      })
    })
    .then(res => res.json())
  }


export const fetchb =  function* (url){
  const res = yield fetch(url)

  const data = yield res.json()

  return data

}
