import {FETCH,DELETE,CREATE,SEARCH,RESET, SAVE} from '../ActionTypes/tablelist';


export const createFetchAction = res => {
           let arr = [];
            let data1 = res.results;
            for(let key in data1){
              arr.push(data1[key])
            }
            let arr1 = [].concat.apply([],arr);
            let arr2 = [];
            for(let i=0; i<arr1.length; i++){
              arr2.push({
                key: arr1[i]._id,
                createdAt: arr1[i].createdAt,
                desc: arr1[i].desc,
                publishedAt: arr1[i].publishedAt,
                who: arr1[i].who,
                type: arr1[i].type,
                url: arr1[i].url,
                used: true,
              });
            }

    return {
        type : FETCH,
        payload: arr2
    }
}

export const createDeleteAction = data => {
      for (let k in data){
        data[k].key = data[k].createdAt
      }
    return{
        type: DELETE,
        payload : data
    }
}

export const CreateAddaction = data => {
  return {
       type: CREATE,
       payload: data
  }
}

export const CreateSearchaction = (value,data) => {

        let arr3 = []
        for(let i in data){

        if(data[i].name.search(value) >= 0){
            arr3.push({
            key: data[i].createdAt,
            createdAt: data[i].createdAt,
            desc: data[i].desc,
            name: data[i].name,
            source: data[i].source,
            url: data[i].url,
            });
        }
      }

  return {
    type:SEARCH,
    payload:arr3
  }
}

export const CreateResetAction = () => {
      return {
        type: RESET,
      }
}

export const CreateSaveAction = (form,key,newData) => {
      form.validateFields((error, row) => {
        if (error) {
          return;
        }
        const index = newData.findIndex(item => key === item.key);
        if (index > -1) {
          const item = newData[index];
          newData.splice(index, 1, {
            ...item,
            ...row,
          });
        } else {
          newData.push(row);
        }
      });
      return {
        type: SAVE,
        payload: newData
      }

}
