import { put, takeEvery } from 'redux-saga/effects';
import { fetcha,fetchc,change,fetch1,datamql} from '../Request/request'



//put 与actiontype相同
//Mysql 请求数据 (初始渲染  重置)
function* fetchUser() {
    const data = yield fetch1()

    yield put({type: "fetch", payload:data});   
}

//MYSQL 删除
function* mysqlDelFectch(val){

    const data2 = yield datamql(val)

    yield put({type: "fetch", payload:data2});
}

//MYSQL 添加
function* mysqlAddFectch(val){

    const data2 = yield datamql(val)

    yield put ({type:'fetch' ,payload:data2})
}

//MYSQL 搜索
function* mysqlSearchFectch(val){

  const data = yield val.data
  yield console.log(data)

  const res= yield fetcha('http://localhost:8080/search',data)


  yield put ({type:'fetch' ,payload:res})

}

//MYSQL 修改
function* mysqlSaveFectch(val){
    const data = yield val.data

    yield fetchc('http://localhost:8080/change',data)

    const data2 = yield fetch1()

    yield put({type:'fetch',payload:data2})
}


//监听与组件type相同
 function* mySaga() {
   yield takeEvery("feh", fetchUser);
   yield takeEvery('del', mysqlDelFectch)
   yield takeEvery('add', mysqlAddFectch)
   yield takeEvery('sear', mysqlSearchFectch)
   yield takeEvery('sav', mysqlSaveFectch)
 }


 export default mySaga;
