import React, {Component} from 'react';
import { Table, Input, InputNumber, Popconfirm, Form, Spin, Button, Modal } from 'antd';
import 'antd/dist/antd.css';
import CollectionCreateForm from '../Create/Create'
import Myinput from '../Input/Input'
import {createFetchAction,createDeleteAction,CreateAddaction} from '../../Actions/tablelist';

import {connect} from 'react-redux';


const EditableContext = React.createContext();



//修改类
class EditableCell extends React.Component {
    getInput = () => {
      if (this.props.inputType === 'number') {
        return <InputNumber />;
      }
      return <Input />;
    };

    renderCell = ({ getFieldDecorator }) => {
      const {
        editing,
        dataIndex,
        title,
        inputType,
        record,
        index,
        children,
        ...restProps
      } = this.props;
      return (
        <td {...restProps}>
          {editing ? (
            <Form.Item style={{ margin: 0 }}>
              {getFieldDecorator(dataIndex, {
                rules: [
                  {
                    required: true,
                    message: `Please Input ${title}!`,
                  },
                ],
                initialValue: record[dataIndex],
              })(this.getInput())}
            </Form.Item>
          ) : (
            children
          )}
        </td>
      );
    };

    render() {

      return <EditableContext.Consumer>{this.renderCell}</EditableContext.Consumer>;
    }
}

//映射状态
const mapStateToProps = (state) => {
    return {
      tablelistReducer : state.tablelistReducer
    }
}

//映射方法
const mapDispatchToProps = {
  createDeleteAction,
  createFetchAction
}

//注入props
@connect(mapStateToProps,)
class EditableTable extends React.Component {
  constructor(props) {
    super(props);

    this.state = { 
      editingKey: '',
      visible: false, 
      loading: false,
      // data: store.getState().tablelistReducer.data
    };

    const {tablelistReducer:{data}} = this.props;

    // const data = this.props.tablelistReducer.data
    this.columns = [
      {
        title: '名称',
        dataIndex: 'who',
        editable: true,
      },
      {
        title: '类型',
        dataIndex: 'type',
        editable: true,
      },
      {
        title: '网址',
        dataIndex: 'url',
        render :text => <a href={text} target='_blank'>链接</a>

      },
      {
        title: '创建时间',
        dataIndex: 'createdAt',
        editable: true,

      },
      {
        title: '发表时间',
        dataIndex: 'publishedAt',
        editable: true,
      },
      {
        title: '内容',
        width: '25%',
        dataIndex: 'desc',
        editable: true,
      },
      {
        title: '修改',
        dataIndex: 'operation',
        render: (text, record) => {
          const { editingKey } = this.state;
          const editable = this.isEditing(record);
          return editable ? (
            <span>
              <EditableContext.Consumer>
                {form => (
                  <a
                    onClick={() => this.save(form, record.key)}
                    style={{ marginRight: 8 }}
                  >
                    保存
                  </a>
                )}
              </EditableContext.Consumer>
              <Popconfirm title="确定要取消吗?" onConfirm={() => this.cancel(record.key)}>
                <a>取消</a>
              </Popconfirm>
            </span>
          ) : (
            <a disabled={editingKey !== ''} onClick={() => this.edit(record.key)}>
              修改
            </a>
          );
        },
      },
      {
        title: '删除',
        dataIndex: 'delete',
        render: (text, record) =>
          data.length >= 0 ? (
            <Popconfirm title="你确定要删除吗？" onConfirm={() => this.handleDelete(record.key)}>
              <a>删除</a>
            </Popconfirm>
          ) : null,
      },
    ];

    // this.setSearchTopStories = this.setSearchTopStories.bind(this);
    this.handleDelete = this.handleDelete.bind(this);
    this.showModal = this.showModal.bind(this);
    this.handleCancel = this.handleCancel.bind(this);
    this.handleCreate = this.handleCreate.bind(this);
    this.saveFormRef = this.saveFormRef.bind(this);
    this.handSearch = this.handSearch.bind(this);
    this.handReset = this.handReset.bind(this);
    // store.subscribe(this.listen);

  }


  isEditing = record => record.key === this.state.editingKey;

  handleDelete = key => {

    //  注入store后使用
    //  store.dispatch(createDeleteAction(key))


     this.props.dispatch(createDeleteAction(key))


    //映射以后使用
    //  this.props.createDeleteAction(key)
  };

  cancel = () => {
    this.setState({ editingKey: '' });
  };

  save(form, key) {
    form.validateFields((error, row) => {
      if (error) {
        return;
      }
      const newData = [...this.state.data];
      const index = newData.findIndex(item => key === item.key);
      if (index > -1) {
        const item = newData[index];
        newData.splice(index, 1, {
          ...item,
          ...row,
        });
        this.setState({ data: newData, editingKey: '' });
      } else {
        newData.push(row);
        this.setState({ data: newData, editingKey: '' });
      }
    });
  }

  edit(key) {
    this.setState({ editingKey: key });
  }

  //添加方法
  handleCancel = () => {
    this.setState({ visible: false });
  };
  handleCreate = () => {
    const { form } = this.formRef.props;
    const { data} = this.state;
    form.validateFields((err, values) => {
      if (err) {
        return;
      }
      let time = (new Date()).valueOf();
      values.key = time;
      data.push(values);
      form.resetFields();
      this.props.dispatch(CreateAddaction(data))
      // this.setState({ data: data, visible: false });
      this.setState({ visible: false });


    });
  };

  showModal = () => {
    this.setState({ visible: true });
  };

  saveFormRef = formRef => {
    this.formRef = formRef;
  };

  //搜索方法
  handSearch(value){
    let { data} = this.state;
    console.log(data)
    let arr3 = []
    for(let i in data){

      if(data[i].who.search(value) >= 0){
        arr3.push({
          key: data[i]._id,
          createdAt: data[i].createdAt,
          desc: data[i].desc,
          publishedAt: data[i].publishedAt,
          who: data[i].who,
          type: data[i].type,
          url: data[i].url,
          used: true,
        });
      }
    }
    this.setState({data:arr3});
  }

  //重置方法
  handReset(){
    let {dataSource} = this.state;
    this.setState({data: dataSource});
  }
  // listen = () => {
  //   this.setState({
  //     data: store.getState().tablelistReducer.data
  //   })
  // };

  //生命周期  加载后
  componentDidMount(){
    // fetch('http://gank.io/api/today')
    // .then(res => res.json())
    // // .then( res => store.dispatch(createFetchAction(res)));
    // .then(res => this.props.createFetchAction(res))
    this.props.dispatch({type:'feh'})
  }


  render() {
    console.log(this)
    // const {data} = this.state
    const components = {
      body: {
        cell: EditableCell,
      },
    };
    const {tablelistReducer:{data}} = this.props;
    const columns = this.columns.map(col => {
      if (!col.editable) {
        return col;
      }
      return {
        ...col,
        onCell: record => ({
          record,
          inputType: col.dataIndex === 'age' ? 'number' : 'text',
          dataIndex: col.dataIndex,
          title: col.title,
          editing: this.isEditing(record),
        }),
      };
    });

    return (
      <div>
        { data
          ?<div>

              <Myinput handSearch={this.handSearch} handReset={this.handReset}></Myinput>
              <Button type="primary" onClick={this.showModal} style={{marginLeft:10}}>
                创建
              </Button>
              <CollectionCreateForm
                wrappedComponentRef={this.saveFormRef}
                visible={this.state.visible}
                onCancel={this.handleCancel}
                onCreate={this.handleCreate}
              />
              <EditableContext.Provider value={this.props.form}>
                <Table
                  components={components}
                  bordered
                  dataSource={data}
                  columns={columns}
                  rowClassName="editable-row"
                  pagination={{
                    onChange: this.cancel,
                  }}
                  rowKey={(data)=>data.key}
                />
              </EditableContext.Provider>
            </div>
          : <Spin style={{marginLeft:'900px',marginTop:'200px'}} tip="加载中..." size='large'/>
        }
      </div>

    );
  }
}

const EditableFormTable = Form.create()(EditableTable);

class Mytable extends Component{ 
    render(){
        return <EditableFormTable />
    }
}



export default Mytable;
