import { put, takeEvery } from 'redux-saga/effects'



function* fetchUser() {
       const res =  yield fetch('http://gank.io/api/today')

       const response = yield res.json()

       let arr = [];
       let data1 = response.results;
       for(let key in data1){
         arr.push(data1[key])
       }
       let arr1 = [].concat.apply([],arr);
       let arr2 = [];
       for(let i=0; i<arr1.length; i++){
         arr2.push({
           key: arr1[i]._id,
           createdAt: arr1[i].createdAt,
           desc: arr1[i].desc,
           publishedAt: arr1[i].publishedAt,
           who: arr1[i].who,
           type: arr1[i].type,
           url: arr1[i].url,
           used: true,
         });
       }
       const data = yield arr2

       yield put({type: "fetch", payload:arr2});

}


 function* mySaga() {
   yield takeEvery("feh", fetchUser);
 }


 export default mySaga;
