import { Button, Modal,  Table, Input, InputNumber, Popconfirm, Form, Spin} from 'antd';
import React from 'react';
import 'antd/dist/antd.css';
import SearchSet from '../Search/Search'
import CollectionCreateForm from '../AddColumns/addcol'
import {observer, inject} from 'mobx-react'


const EditableContext = React.createContext();

class EditableCell extends React.Component {
  getInput = () => {
    if (this.props.inputType === 'number') {
      return <InputNumber />;
    }
    return <Input />;
  };

  renderCell = ({ getFieldDecorator }) => {
    const {
      editing,
      dataIndex,
      title,    
      inputType,
      record,
      index,
      children,
      ...restProps
    } = this.props;
    return (
      <td {...restProps}>
        {editing ? (
          <Form.Item style={{ margin: 0 }}>
            {getFieldDecorator(dataIndex, {
              rules: [
                {
                  required: true,
                  message: `Please Input ${title}!`,
                },
              ],
              initialValue: record[dataIndex],
            })(this.getInput())}
          </Form.Item>
        ) : (
          children
        )}
      </td>
    );
  };

  render() {
    return <EditableContext.Consumer>{this.renderCell}</EditableContext.Consumer>;
  }
}

//可编辑表格 父组件
@inject('store')
@observer
class EditableTable extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      visible: false, 
      searchText: '',
      searchedColumn: '', 
      dataSearch:null,
    };
    const {home:{handleDelete}}= this.props.store;
    const {home:{editingKey,isEditing,save,cancel,edit}} = this.props.store;
    this.columns = [
      {
        title: 'Name',
        dataIndex: 'who',
        editable: true,
      },
      {
        title:'Description',
        dataIndex:'desc',
        width:'30%',
        editable: true,
        ellipsis: true,
      },
      {
        title: 'Type',
        dataIndex: 'type',
      },
      {
        title:'Link',
        dataIndex:'url',
        editable: true,
        render: text => <a href={text} target="_blank">链接</a>,
      },
      {
        title: 'CreatedAt',
        dataIndex: 'createdAt',
        width:'15%',
      },
      {
        title: 'Source',
        dataIndex: 'source',
        editable: true,
      },
      {
        title: 'Delete',
        dataIndex: 'Delete',
        render: (text, record) =>
          this.props.store.home.data.length >= 1 ? (
            <Popconfirm title="Sure to delete?" onConfirm={() => handleDelete(record.key)}>
              <a>Delete</a>
            </Popconfirm>
          ) : null,
      },  

      {
        title: 'Operation',
        dataIndex: 'operation',
        render: (text, record) => {



          const editable = isEditing(record);
          return editable ? (
            <span>
              <EditableContext.Consumer>
                {form => (
                  <a
                    onClick={() =>save(form, record.key)}
                    style={{ marginRight: 8 }}
                  >
                    Save
                  </a>
                )}
              </EditableContext.Consumer>
              <Popconfirm title="Sure to cancel?" onConfirm={() => cancel(record.key)}>
                <a>Cancel</a>
              </Popconfirm>
            </span>
          ) : (
            <a disabled={editingKey !== ''} onClick={() => edit(record.key)}>
              Edit
            </a>
          );
        },
      },
    ];
    this.handleSearch = this.handleSearch.bind(this)
    this.handleReset = this.handleReset.bind(this)
  }


  showModal = () => {
    this.setState({ visible: true });
  };

  handleCancel = () => {
    this.setState({ visible: false });
  };

  handleCreate = () => {
    const {data} = this.state;
    const { form } = this.formRef.props;
    form.validateFields((err, values) => {
      if (err) {
        return;
      }
      let time = new Date();
      let time2 = (new Date().valueOf())
      function formatDate(now) { 
        var year=now.getFullYear(); 
        var month=now.getMonth()+1; 
        var date=now.getDate(); 
        var hour=now.getHours(); 
        var minute=now.getMinutes(); 
        var second=now.getSeconds(); 
        return year+"-"+month+"-"+date+" "+hour+":"+minute+":"+second; 
     } 
      var d =(formatDate(time))
      values.key = time2
      values.createdAt = d
      console.log('Received values of form: ', values);
      data.push(values)
      this.setState({data: data})
      form.resetFields();
      this.setState({ visible: false });
    });
  };

  saveFormRef = formRef => {
    this.formRef = formRef;
  };

  changeData(result){
    let arr = []
    let data2 = []
    const data1 = result.results

    for(let k in data1){
        arr.push(data1[k])
    }
    let arr2 = [].concat.apply([], arr);
    console.log(arr2)

    for (let i = 0; i < arr2.length; i++) {
        data2.push({
            publishedAt:arr2[i].publishedAt,
            type:arr2[i].type,
            url:arr2[i].url,	
            who:arr2[i].who,
            key: arr2[i]._id,
            createdAt:arr2[i].createdAt,
            desc:arr2[i].desc,
            source:arr2[i].source,


            used:true,
        });
   }

    this.setState({
      data : data2,
      dataSearch: data2
    })
  }

  componentDidMount(){
  const {store} = this.props;
  const {home:{changeData}} = store;
  fetch('http://gank.io/api/today')
    .then(result => result.json())
    .then(result => changeData(result,this))
    // .catch(e => this.setState({ error: e }))
  }

  onSearch(value){
    this.setState({searchText:value})
    const { searchText } = this.state
    console.log(searchText)
    // this.setState({data :data })
  }


 //还原筛选前的数据
 handleReset(){
   const {dataSearch} = this.state
   this.setState({data:dataSearch,
  })
 }
 //筛选数据
  handleSearch(values){
    console.log(values);
    let arr6 = []
    const {data} = this.state
    for (let k in data){
      if (data[k].who.search(values)>=0){
        arr6.push({
          createdAt: data[k].createdAt,
          desc: data[k].desc,
          key: data[k].key,
          publishedAt: data[k].publishedAt,
          source: data[k].source,
          type: data[k].type,
          url: data[k].url,
          who: data[k].who,
        })
      }
    }
     this.setState({
       data:arr6,
    })
};


  render() {
    const {store} = this.props
    const {home:{data,isEditing}} = store
    const components = {
      body: {
        cell: EditableCell,
      },
    };

    const columns = this.columns.map(col => {
      if (!col.editable) {
        return col;
      }
      return {
        ...col,
        onCell: record => ({
          record,
          inputType: col.dataIndex === 'age' ? 'number' : 'text',
          dataIndex: col.dataIndex,
          title: col.title,
          editing: isEditing(record),
        }),
      };
    });
    return (
        <div>
           <div>
            <SearchSet handleSearch= {this.handleSearch} handleReset = {this.handleReset}/> 
            <Button  type="primary" onClick={this.showModal} style={{margin:20}}>
              Add a row
            </Button>
            <CollectionCreateForm
                wrappedComponentRef={this.saveFormRef}
                visible={this.state.visible}
                onCancel={this.handleCancel}
                onCreate={this.handleCreate}
            />
            <EditableContext.Provider value={this.props.form}>
                <Table
                style={{margin:20 }}
                components={components}
                bordered
                dataSource={data}
                columns={columns}
                rowClassName="editable-row"
                pagination={{
                onChange: this.cancel,
                }}
                rowKey= {data => data.key}
                />
            </EditableContext.Provider>
      </div>
    </div>
    );
  }
}

const EditableFormTable = Form.create()(EditableTable);

//导出的组件
class Editable extends React.Component {
    render(){
        return <EditableFormTable />
    }

}

export default Editable
