import { Form, Row, Col, Input, Button, Icon } from 'antd';
import React from 'react';
import './App.css';
import 'antd/dist/antd.css';

class AdvancedSearchForm extends React.Component {
    state = {
      expand: false,
    };

    // To generate mock Form.Item
    getFields() {
      const count = this.state.expand ? 6 : 2;
      const { getFieldDecorator } = this.props.form;
      const children = [];
        children.push(
          <Col span={8} key={1} >
            <Form.Item label={"Name"}>
              {getFieldDecorator("who", {
                rules: [
                  {
                    required: true,
                    message: 'Input something!',
                  },
                ],
              })(<Input placeholder="placeholder" />)}
            </Form.Item>
            <Form.Item label={"Description"} key={2} >
              {getFieldDecorator("desc", {
                rules: [
                  {
                    required: true,
                    message: 'Input something!',
                  },
                ],
              })(<Input placeholder="placeholder" />)}
            </Form.Item>
          </Col>,
        // <Col span={8} key={2} style={{ display: 2 < count ? 'block' : 'none' }}>
        //     <Form.Item label={"Description"} key={2} style={{ display: 2 < count ? 'block' : 'none' }} >
        //       {getFieldDecorator("desc", {
        //         rules: [
        //           {
        //             required: true,
        //             message: 'Input something!',
        //           },
        //         ],
        //       })(<Input placeholder="placeholder" />)}
        //     </Form.Item>
        //   </Col>,
        );
      return children;
    }

    handleSearch = e => {
      e.preventDefault();
      this.props.form.validateFields((err, values) => {
        console.log('Received values of form: ', values);
      });
    };

    handleReset = () => {
      this.props.form.resetFields();
    };

    toggle = () => {
      const { expand } = this.state;
      this.setState({ expand: !expand });
    };

    render() {
      return (
        <Form className="ant-advanced-search-form" onSubmit={this.handleSearch} style={{margin:20}}>
          <Row gutter={24}>{this.getFields()}</Row>
          <Row>
            <Col span={24} style={{ textAlign: 'right' }}>
              <Button type="primary" htmlType="submit">
                Search
              </Button>
              <Button style={{ marginLeft: 8 }} onClick={this.handleReset}>
                Clear
              </Button>
              <a style={{ marginLeft: 8, fontSize: 12 }} onClick={this.toggle}>
                Collapse <Icon type={this.state.expand ? 'up' : 'down'} />
              </a>
            </Col>
          </Row>
        </Form>
      );
    }
  }

const WrappedAdvancedSearchForm = Form.create({ name: 'advanced_search' })(AdvancedSearchForm);
class Myfrom extends React.Component {
    render(){
        return(
         <div>
            <WrappedAdvancedSearchForm />
          </div>
        )

    }

}

export default Myfrom
