import { Form, Row, Col, Input, Button, Icon } from 'antd';
import React from 'react';
import './App.css';
import 'antd/dist/antd.css';

class AdvancedSearchForm extends React.Component {
    constructor(props){
        super(props)
        this.state = {
            expand: false,
          };
    }

    // To generate mock Form.Item
    getFields() {
      const { getFieldDecorator } = this.props.form;
      const children = [];
        children.push(
          <Col span={8} key={1} >
            <Form.Item label={"Name"}>
              {getFieldDecorator("who", {
                rules: [
                  {
                    required: true,
                    message: 'Input something!',
                  },
                ],
              })(<Input placeholder="placeholder" />)}
            </Form.Item>
            <Form.Item label={"Description"} key={2} >
              {getFieldDecorator("desc", {
                rules: [
                  {
                    message: 'Input something!',
                  },
                ],
              })(<Input placeholder="placeholder" />)}
            </Form.Item>
          </Col>,
        );
      return children;
    }

    handleSearch = e => {
      e.preventDefault();
      this.props.form.validateFields((err, values) => {
        const {data}= this.props

        let arr1 = []
        let arr3 = []
        for (let k in data){
          arr1.push(data[k].who)
          if (arr1[k] == values){
            arr3.push({
              createdAt: arr1[k].createdAt,
              desc: arr1[k].desc,
              key: arr1[k].key,
              publishedAt: arr1[k].publishedAt,
              source: arr1[k].source,
              type: arr1[k].type,
              url: arr1[k].url,
              who: arr1[k].who,
            })
          }
        }
        console.log(arr3)
      });
      // const {data}= this.props

      // let arr1 = []
      // let arr3 = []
      // for (let k in data){
      //   arr1.push(data[k].who)
      //   if (arr1[k].who == values){
      //     arr3.push({
      //       createdAt: arr1[k].createdAt,
      //       desc: arr1[k].desc,
      //       key: arr1[k].key,
      //       publishedAt: arr1[k].publishedAt,
      //       source: arr1[k].source,
      //       type: arr1[k].type,
      //       url: arr1[k].url,
      //       who: arr1[k].who,
      //     })
      //   }
      // }
      // console.log(arr3)
     };

    handleReset = () => {

      this.props.form.resetFields();
    };

    toggle = () => {
      const { expand } = this.state;
      this.setState({ expand: !expand });
    };



    render() {
        const data = this.props
        console.log();
      return (
        <Form className="ant-advanced-search-form" onSubmit={this.handleSearch} style={{margin:20}}>
          <Row gutter={24}>{this.getFields()}</Row>
          <Row>
            <Col span={24} style={{ textAlign: 'right' }}>
              <Button type="primary" htmlType="submit">
                Search
              </Button>
              <Button style={{ marginLeft: 8 }} onClick={this.handleReset}>
                Clear
              </Button>
              <a style={{ marginLeft: 8, fontSize: 12 }} onClick={this.toggle}>
                Collapse <Icon type={this.state.expand ? 'up' : 'down'} />
              </a>
            </Col>
          </Row>
        </Form>
      );
    }
  }

const WrappedAdvancedSearchForm = Form.create({ name: 'advanced_search' })(AdvancedSearchForm);

class Myform extends React.Component {

    render(){
        const {data}= this.props
        return(
         <div>
            <WrappedAdvancedSearchForm data= {data}/>
          </div>
        )

    }

}

export default Myform
