import { Form, Row, Col, Input, Button, Icon } from 'antd';
import React from 'react';
import './App.css';
import 'antd/dist/antd.css';

class AdvancedSearchForm extends React.Component {
    constructor(props){
        super(props)
        this.state = {
            expand: false,
          };
    }

    // To generate mock Form.Item
    getFields() {
      const { getFieldDecorator } = this.props.form;
      const children = [];
        children.push(
          <Col span={8} key={1} >
            <Form.Item label={"Name"}>
              {getFieldDecorator("who", {
                rules: [
                  {
                    required: true,
                    message: 'Input something!',
                  },
                ],
              })(<Input placeholder="placeholder" />)}
            </Form.Item>
            <Form.Item label={"Description"} key={2} >
              {getFieldDecorator("desc", {
                rules: [
                  {
                    message: 'Input something!',
                  },
                ],
              })(<Input placeholder="placeholder" />)}
            </Form.Item>
          </Col>,
        );
      return children;
    }

    // handleSearch = e => {
    //   e.preventDefault();
    //   this.props.form.validateFields((err, values) => {
    //     const {data}= this.props
    //     let arr1 = []
    //     for (let k in data){
    //       if (data[k].who == values.who){
    //         arr1.push({
    //           createdAt: data[k].createdAt,
    //           desc: data[k].desc,
    //           key: data[k].key,
    //           publishedAt: data[k].publishedAt,
    //           source: data[k].source,
    //           type: data[k].type,
    //           url: data[k].url,
    //           who: data[k].who,
    //         })
    //       }
    //     }
    //     this.setState({data:arr1})
    //   });

    //  };

    // handleReset = () => {

    //   this.props.form.resetFields();
    // };

    toggle = () => {
      const { expand } = this.state;
      this.setState({ expand: !expand });
    };



    render() {
      const {handleSearch} = this.props;
      const {handleReset} = this.props;
      return (
        <Form className="ant-advanced-search-form" onSubmit={handleSearch} style={{margin:20}}>
          <Row gutter={24}>{this.getFields()}</Row>
          <Row>
            <Col span={24} style={{ textAlign: 'right' }}>
              <Button type="primary" htmlType="submit">
                Search
              </Button>
              <Button style={{ marginLeft: 8 }} onClick={handleReset}>
                Clear
              </Button>
              <a style={{ marginLeft: 8, fontSize: 12 }} onClick={this.toggle}>
                Collapse <Icon type={this.state.expand ? 'up' : 'down'} />
              </a>
            </Col>
          </Row>
        </Form>
      );
    }
  }

