import {  Input ,Button} from 'antd';
import React from 'react';
import 'antd/dist/antd.css';
import {observer, inject} from 'mobx-react'


const { Search } = Input;

@inject('store')
@observer
class SearchSet extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      value:""
    }
    this.handleResetson = this.handleResetson.bind(this)
  }

 handleSearchIn(value){
    setTimeout(()=> {this.props.handleSearch(value);},1500)
}

 handleChange(e){
    this.setState({
      value:e.target.value
    })
  }
 handleResetson(){
   this.props.handleReset()
    this.setState({
      value:""
    })
  }

  render(){
    const {store} = this.props;
    const {home:{handleSearchIn,handleResetson,handleChange,value}} = store;
    return(
      <div>
      <Search
        placeholder="input search text"
        enterButton="Search"
        size="large"
        onSearch={value => handleSearchIn(value)}
        onChange={e => this.handleChange(e)}
        style={{margin:20 ,width:"30%" }}
        value={value}
      />
      <Button onClick={handleResetson} style={{ width:90}} style={{margin:20}}>
       Reset
      </Button>
    </div>
    )
  }

}

export default SearchSet
