import {observable, action,  computed} from "mobx";


export default class HomeStore {
    @observable name = '张三';
    @observable age = 100;
    @observable price = 200;
    @observable data = null;
    @observable dataSearch = null;

    @computed get aaa(){
        return this.age * this.price
    }

    @action click = ()=> {
        this.name ='test';
        this.age = 500;
    }

    //获取数据 转为一维数组
    @action changeData(result){
      console.log(result)
      let arr = []
      let data2 = []
      const data1 = result.results

      for(let k in data1){
          arr.push(data1[k])
      }
      let arr2 = [].concat.apply([], arr);
      console.log(arr2)

      for (let i = 0; i < arr2.length; i++) {
          data2.push({
              publishedAt:arr2[i].publishedAt,
              type:arr2[i].type,
              url:arr2[i].url,	
              who:arr2[i].who,
              key: arr2[i]._id,
              createdAt:arr2[i].createdAt,
              desc:arr2[i].desc,
              source:arr2[i].source,


              used:true,
          });
     }
      this.props.data = data2
      this.dataSearch= data2

    }
}
