import {  Input ,Button} from 'antd';
import React from 'react';
import 'antd/dist/antd.css';


const { Search } = Input;

class SearchSet extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      value:""
    }
  }

 handleSearchIn(value){
    setTimeout(()=> {this.props.handleSearch(value);},1500)
}

 handleChange(e){
    this.setState({
      value:e.target.value
    })
  }
 handleResetson(){
   const {handleReset} = this.props
    handleReset()
    this.setState({
      value:""
    })
  }

  render(){
    const {value} = this.state;
    return(
      <div>
      <Search
        placeholder="input search text"
        enterButton="Search"
        size="large"
        onSearch={value => this.handleSearchIn(value)}
        onChange={e => this.handleChange(e)}
        style={{margin:20 ,width:"30%" }}
        value={value}
      />
      <Button onClick={this.handleResetson} style={{ width:90}} style={{margin:20}}>
       Reset
      </Button>
    </div>
    )
  }

}

export default SearchSet
